import React from 'react'

class AddDelivery extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            addingToDo: false,
            title: ''
        }
    }

    newTodo = () => {
        this.setState(() => {
            return { addingToDo: true }
        })
    }

    titleChange = (event) => {
        this.setState({title: event.target.value, addres: event.target.value})
    }

    addDelivery =() =>{
        this.props.addNewD(this.state.title,this.state.address)
        this.setState({title:'', address: '', addingToDo: false})
    }

    render() {
        if (this.state.addingToDo) {
            return (
                <>
                    <span>
                        Name:
                    </span>
                    <input value={this.state.title} onChange={this.titleChange} type="text" />
                    <span>
                        Address:
                    </span>
                    <input value={this.state.address} onChange={this.addressChange} type="text" />
                    <button onClick={this.addDelivery}>Add Delivery</button>
                </>
            )
        } else {
            return (
                <>
                    <button onClick={this.newTodo}>New Delivery +</button>
                </>
            )
        }
    }

}

export default AddDelivery
