import React from 'react'
import Todolist from './TodoList'
import AddTodo from './AddTodo'
import Search from './Search'

class Todos extends React.Component {
    constructor(props){
        super(props)

        this.state = {
            todos:[
                {name: 'learn react', completed: false},
                {name: 'walk the dog', completed: false},
                {name: 'watch the football game', completed: false}
            ],
            currentFilter: ''

        }
    }

    componentDidMount(){
        fetch('https://jsonplaceholder.typicode.com/todos')
                .then(response => response.json() )
                .then(json => this.setState({todos: json}))
    }

    addTodoItem = (title) => {
        this.setState(state =>{
            return {
                todos: [...state.todos, {title: title, completed: false}]
            }
        })
    }

    visibleTodos(){
        if(this.state.currentFilter !==''){
            return this.state.todos.filter(todo => todo.title.includes(this.state.currentFilter))
        } else{
            return this.state.todos
        }
    }

    updateFilter = (filter) => {
        this.setState({currentFilter: filter})
    }

    render(){
        const visible = this.visibleTodos()
        return (
            <div>
                <h1>Todos</h1>
                <AddTodo addTodoItem={this.addTodoItem} />
                <Search updateFilter={this.updateFilter}/>
                <Todolist items={visible}/>
            </div>
        )
    }
}

export default Todos
