import React from 'react'

class Search extends React.component {
    constructor(props) {
        super(props)

        this.state = {
            searchTerm: ''
        }
    }

    searchChange = (event) => {
        this.setState({ searchTerm: event.target.value })
    }

    submitSearch = () => {
        this.props.addTodoItem(this.state.name)
        this.setState({ name: '', addingToDo: false })
    }

    render() {
        return (
            <>
                <span>
                    searchTerm:
                </span>
                <input value={this.state.searchTerm} onChange={this.searchChange} type="text" />
                <button onClick={this.submitSearch}>Add Todo</button>
            </>
        )
    }
}

export default Search
