import React from 'react'

class AddDelivery extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            addingToDo: false,
            address: '',
            price: ''
        }
    }

    newTodo = () => {
        this.setState(() => {
            return { addingToDo: true }
        })
    }
    addressChange = (event) => {
        this.setState({address: event.target.value})
    }
    priceChange = (event) => {
        this.setState({price: event.target.value})
    }
    

    addDelivery =() =>{
        this.props.addNewD(this.state.address, this.state.address)
        this.setState({address:'', price: '', addingToDo: false})
    }

    render() {
        if (this.state.addingToDo) {
            return (
                <>
                    <span>
                        Address:
                    </span>
                    <input value={this.state.address} onChange={this.addressChange} type="text" />
                    <span>
                        Price:
                    </span>
                    <input value={this.state.price} onChange={this.priceChange} type="text" />
                    <button onClick={this.addDelivery}>Add Delivery</button>
                </>
            )
        } else {
            return (
                <>
                    <button onClick={this.newTodo}>New Delivery +</button>
                </>
            )
        }
    }

}

export default AddDelivery
