import React from 'react'

const TodoList = (props) => {
    return (
        <ol>
            {props.items.map((todo, i) => {
                let button;
                if(props.showButton) {
                    button = props.buttonRender(i);
                }
                return (
                    <div>
                        {<li key={i}>{todo.title} {button ? button : null}</li>}
                    </div>
                )
            })}
        </ol>
    );
}

export default TodoList
