  //       <MenuItem name="account" onClick={handleClose}>My account</MenuItem>
  //       <MenuItem name="logout" onClick={handleClose}>Logout</MenuItem>
  //     </Menu>
  //   </div>
  // );

  return (
    <select onChange={event => props.selectItem(event.target.name)}>
      <option name="profile">profile</option>
      <option name="account">account</option>
      <option name="logout">logout</option>
    </select>
  )
}
