import React from 'react'
import Dlist from './DList'
import AddDelivery from './AddDelivery'
import Search from './Search'

class Deliveries extends React.Component {
    constructor(props){
        super(props)

        this.state = {
            todos: [],
            currentFilter: ''

        }
    }

    componentDidMount(){
        fetch('https://google.com')
                .then(response => response.json() )
                .then(json => this.setState({todos: json}))
    }

    addNewD = (title, address) => {
        this.setState(state =>{
            return {
                todos: [...state.todos, {title: title, address: address, completed: false}]
            }
        })
    }

    visibleTodos(){
        if(this.state.currentFilter !==''){
            return this.state.todos.filter(todo => todo.address.includes(this.state.currentFilter))
        } else{
            return this.state.todos
        }
    }

    updateFilter = (filter) => {
        this.setState({currentFilter: filter})
    }

    render(){
        const visible = this.visibleTodos()
        return (
            <div>
                <h1>Delivery list</h1>
                <AddDelivery addNewD={this.addNewD} />
                <br></br>
                <Search updateFilter={this.updateFilter}/>
                <Dlist items={visible}/>
            </div>
        )
    }
}

export default Deliveries
