import React from 'react';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

export default function SimpleMenu(props) {
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = event => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = (event) => {
    console.log('event', event.target)
    setAnchorEl(null);
    props.selectItem(event.target.value)
  };

  return (
    <div>
      <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
        Open Menu
      </Button>
      <Menu
        id="simple-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        <MenuItem value="profile" onClick={handleClose}>Profile</MenuItem>
        <MenuItem value="account" onClick={handleClose}>My account</MenuItem>
        <MenuItem value="logout" onClick={handleClose}>Logout</MenuItem>
      </Menu>
    </div>
  );
}
